PaymentMethods.PaymentMethodsHTML:
@using (Html.BeginForm()){
    <div class="paymentMethods form">
        <div class="paymentMethodsList">
            <div class="paymentMethodTit">
                <div class="paymentMethod-select">
                    @Html.ValidationSummary()
                    @foreach(var Categoria in Model.PaymentCategories){
                     <div class="box-metodos-pagamento">
                        <a href="javascript: void(0)" class="boxMethod-select btn-secondary" categoryid="@Categoria.Id">
                          <strong>@Categoria.Name</strong>
                        </a>                    
                        <div class="paymentMethod-box" categoryid="@Categoria.Id">                  
                            <ul>
                                @foreach(var Metodo in Categoria.PaymentMethods){
                                    <li class="paymentMethodsList-method"> 
                                        <div class="paymentMethodsList-method-display">
                                            <span style="display: none;">
                                                @Html.InputRadio("PaymentMethod.Id", Metodo.Id, Metodo.Selected)
                                            </span>
                                            <img src="@Metodo.MainImageURL" alt="@Metodo.DisplayName" />
                                        </div>   
                                    </li> 
                                }
                                @foreach(var Metodo in Categoria.PaymentMethods){

                                    <div class="paymentMethodContent">
                                            @if(Cart.Total>0 && Metodo.Id==95){
                                                continue;               
                                            }                                      		
                                            <div class="paymentMethod paymentMethod@(Metodo.Id)" method="@Metodo.Id">
                                      			<div class="paymentMethod-Title">@Metodo.DisplayName</div>
                                                @if(Metodo.HasPayInFullDiscount){
                                                    <div class="paymentMethod-discount">
                                                        <small>Pague somente @Metodo.PayInFullDiscountValue.AsMoney() - (@Metodo.PayInFullDiscountPercent% de desconto)</small>
                                                    </div>
                                                }
                                                @if(Metodo.Help!=""){
                                                    <div class="paymentMethod-text">@Metodo.Help</div>
                                                }
                                                @if(Metodo.HasForm){
                                                    <div class="paymentMethod-form">
                                                        @foreach(var Field in Metodo.PaymentForm.Fields){
                                                            if(Field.Id=="SecurityCode"){
                                                                <div class="paymentMethod-field parent-validation">
                                                                    <label><span class="paymentMethod-fieldSpan">@Field.LabelText</span> @Html.Input(Field).AddClass(Field.Id) <img src="imagens/codSeguranca.png" />@Html.ValidateMessage(Field)<span class="erros">Ops, que tal verificar este campo</span></label>
                                                                </div>                                  
                                                            }else if(Field.Id=="ExpirationMonth"){
                                                                @:<div class="paymentMethod-field parent-validation">
                                                                    @:<label><span class="paymentMethod-fieldSpan">Validade</span> @Html.Input(Field).AddClass(Field.Id)<span class="erros">Ops, que tal verificar este campo</span></label>
                                                            }else if(Field.Id=="ExpirationYear"){
                                                                    @:<label>@Html.Input(Field) @Html.ValidateMessage(Field)<span class="erros">Ops, que tal verificar este campo</span></label>
                                                                @:</div>                            
                                                            }
                                                            else{
                                                                <div class="paymentMethod-field parent-validation">
                                                                    <label>
                                                                        <span class="paymentMethod-fieldSpan">@Field.LabelText</span> @Html.Input(Field).AddClass(Field.Id) <span class="paymentMethod-fieldExemplo"></span> @Html.ValidateMessage(Field)
                                                                        <span class="erros">Ops, que tal verificar este campo</span>
                                                                    </label>
                                                                </div>                                  
                                                            }
                                                        }                                                                           
                                                    </div>
                                                }     

                                                @if(Metodo.HasInstallments){
                                                    <div class="paymentMethod-parcel">
                                                        @Html.InputDropdown("PaymentInstallments.Quantity", Metodo.PaymentInstallments,"Selecione o parcelamento")
                                                    </div>
                                                }
                                                <div class="paymentMethod-buttom">
                                                   <input type="button" id="enviar" name="enviar" value="concluir compra" class="bt-principal text-center btn-primary" style="" />
                                                </div>
                                            </div>
                                    </div>                                  
                                }
                            </ul>
                        </div> 
                      </div>                        
                    }                   
                </div>
            </div>          
        </div>
    </div>
}

