ScriptRazor.ScriptRazorHTML:
@{
	var cartUrl = "//www9.ciashop.com.br/magazine25/carrinho";
	var buildCartURL = cartUrl+"/?sku="+System.Web.HttpContext.Current.Request.QueryString["sku"]+"&qtd="+System.Web.HttpContext.Current.Request.QueryString["qtd"];
}

<script>
function buildCart(){
	$(".ajaxLoader").show();
    $.ajax({
        url: "@buildCartURL",
		cache: false,
        success: function(){
  			setTimeout(function(){window.location = "@(cartUrl)/t/@(Cart.TemplateId)"},1000);
        }
    });   
}

function clearAndBuildCart(){
	$(".ajaxLoader").show();
    AjaxRequestDispatcher('btnEsvaziar');
	setTimeout(function(){buildCart()},3000);    
}

</script>

@if(Cart.Items.Count>0){
	<div class="buildCart">
      	<div class="alert w-65">
            <strong>Atenção!</strong> Verificamos que você já possuí alguns produtos adicionados ao seu carrinho. Escolha abaixo se você deseja adicionar os novos itens ao seu carrinho atual ou limpar o seu carrinho antes de adicionar os novos itens.
        </div>
		<div class="form-box w-65">
          	<h2 class="form-box-heading">
              Seu Carrinho
          	</h2>
			<div class="miniCart-table">
			    <div class="miniCart-items">
			      <table class="table">
			        <tbody>
			          @foreach(var item in Cart.Items){
			              <tr class="table-item">
			                  <td class="cart-product-image" width="90"><img class="photo-product" src="@(item.Image)?width=80&height=80" alt="[A4]" width="60" height="60" /></td>
			                <td class="cart-product-name"><strong>@item.Name</strong><div class="miniCart-product-quantity">Quantidade: @item.Quantity</div></td> 
			                  <td class="cart-product-subtotalPrice">@item.TotalPrice.AsMoney()</td>
			              </tr>
			          }
			        </tbody>
			      </table>
			    </div>
			</div>	
			<div class="form-box-bt">
				<a href="javascript: buildCart();" class="bt-small-secundary to-left">Adicionar Produtos</a>
		        <a href="javascript: clearAndBuildCart();" class="bt-small-primary to-right">Limpar Carrinho e Adicionar Produtos</a>
		    </div>
		</div>
	</div>
}else{
	<script>
		buildCart();
	</script>	
}
<div class="ajaxLoader"></div>
